import React, { useState } from 'react'

const Cart = ({cartItems, counts}) => {

  const handlePlusClick = (itemId) => {
    setCounts((prevCounts) => ({
      ...prevCounts,
      [itemId]: (prevCounts[itemId] || 0) + 1,
    }));
  };

  const handleMinusClick = (itemId) => {
    setCounts((prevCounts) => ({
      ...prevCounts,
      [itemId]: Math.max((prevCounts[itemId] || 0) - 1, 0),
    }));
  };

  const handleAddToCart = (itemId, title, price) => {
    if (counts[itemId] > 0) {
      addCart({ id: itemId, title, price, quantity: counts[itemId] });
      setCounts((prevCounts) => ({
        ...prevCounts,
        [itemId]: 0, 
      }));
    }
  };

  return (
    <>

<div className='cart'>
  <form className="cart-form">
    <h1 style={{textAlign: 'center'}}>добавление товары</h1>
      {cartItems.map(({ id, title, price}) => (
            <div key={id}>
              <div style={{
                fontSize: '12px',
                marginTop: '10px'
              }}>
                {title}
              </div>

               <span style={{
                  fontSize: '15px',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  cursor: 'pointer'
                }}>Price: {price}
                <span>{counts}</span>
               </span> 

            </div>
          ))}
        <div>
          <input placeholder="+7 (__) ___ __ - __" type="tel" />
          <button type="submit">Order</button>
        </div>
  </form>
</div>

    </>
  )
}

export default Cart
