import React, { useState } from 'react'
import Header from './components/Header'
import Feedback from './components/Feedback'
import Cart from './components/Cart'
import Items from './components/Items'


const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [counts, setCounts] = useState({});

  const addToCart = (item) => {
    if (cartItems.length < 6) {
      setCartItems([...cartItems, item]);
    } else {
      alert('Maximum 6 items allowed in the cart');
    }
  };

  const handlePlusClick = (itemId) => {
    setCounts((prevCounts) => ({
      ...prevCounts,
      [itemId]: (prevCounts[itemId] || 0) + 1,
    }));
  };

  const handleMinusClick = (itemId) => {
    setCounts((prevCounts) => ({
      ...prevCounts,
      [itemId]: Math.max((prevCounts[itemId] || 0) - 1, 0),
    }));
  };

  const handleAddToCart = (itemId, title, price) => {
    if (counts[itemId] > 0) {
      addCart({ id: itemId, title, price, quantity: counts[itemId] });
      setCounts((prevCounts) => ({
        ...prevCounts,
        [itemId]: 0, 
      }));
    }
  };

  return (
    <>
    <Header />
      <Feedback />
      <Cart 
        cartItems={cartItems} 
        setCartItems={setCartItems} 
        counts={counts}
      />
      <Items addCart={addToCart} counts={counts} setCounts={setCounts}/>
    </>
  )
}

export default App
