import React from 'react'

const Cart = ({cartItems, counts}) => {

  return (
    <>

<div className='cart'>
  <form className="cart-form">
    <h1 style={{textAlign: 'center'}}>добавление товары</h1>
      {cartItems.map(({ id, title, price}) => (

            <div key={id}>

              {counts[id] && <div style={{
                fontSize: '12px',
                marginTop: '10px'
              }}>
                {title}
                <span style={{
                  fontSize: '15px',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  cursor: 'pointer'
                }}>Price: {price}
               </span> 
                <span>{counts[id]}</span>
              </div>}


            </div>

          ))}
        <div>
          <input placeholder="+7 (__) ___ __ - __" type="tel" />
          <button type="submit">Order</button>
        </div>
  </form>
</div>

    </>
  )
}

export default Cart
